pragma solidity ^0.8.20;

contract DungeonEvents {
    event RoomDiscovered(uint256 indexed location, uint64 blockNumber, uint8 direction);
    event RoomActualised(uint256 indexed location, bytes32 blockHash, uint8 exits, uint8 kind);
    event CharacterMoved(
        uint256 indexed characterId,
        uint256 indexed oldLocation,
        uint256 indexed newLocation,
        uint8 mode,
        uint256 path
    );
    event Enter(uint256 indexed characterId, address indexed player, string name);
    event Death(uint256 indexed characterId, uint256 monsterId);
    event LevelUp(uint256 indexed characterId, uint16 newLevel);
    event Equip(uint256 characterId, uint256 gearId, uint8 slotType);
    event Resurrect(uint256 indexed deadCharacterId, uint256 newCharacterId);
    event Heal(uint256 indexed characterId, uint16 hp);
    event RandomEvent(uint256 indexed areaLocation, uint64 blockNumber);
    event Recycle(uint256 indexed characterId, uint256 gearId);
    event QuestUpdate(uint256 indexed characterId, uint64 indexed id, uint8 indexed status, string data);
    event RoomTaxPay(address indexed owner, uint256 tax, uint256 newDueDate);
    event RoomIncome(uint256 indexed location, address indexed owner, uint256 id, uint256 amount);
    event RoomName(uint256 indexed location, string name, uint256 characterId);
}
","keccak256":"0xf66026ea03c69423579146b584b537cce0b04de850375c415e1eac548fcb7b58","license":"MIT"},"src/dungeon/DungeonFacetBase.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "../diamond/DiamondStorageContract.sol";
import "./DungeonDataLayout.sol";
import "./DungeonEvents.sol";
import "./PureDungeon.sol";
import "../utils/BlockHashRegister.sol";
import "../characters/Characters.sol";
import "../tokens/Elements.sol";
import "../tokens/Gears.sol";
import "../player/Player.sol";

abstract contract DungeonFacetBase is DungeonDataLayout, DungeonEvents, DiamondStorageContract {
    uint256 internal constant MAX_GEARS = 10;


    modifier onlyOwner() {
        DiamondStorage storage ds = diamondStorage();
        require(msg.sender == ds.contractOwner, "Only owner is allowed to perform this action");
        _;
    }

    modifier onlyAdmin() {
        require(msg.sender == _adminContract, "NOT_AUTHORIZED_ADMIN");
        _;
    }

    modifier onlyPlayer() {
        require(msg.sender == address(_playerContract), "only players allowed");
        _;
    }

    function _actualiseRoom(uint256 location) internal {
        Room storage room = _rooms[location];
        require(room.blockNumber > 0, "room not created yet");
        if (room.kind == 0) {
            bytes32 blockHash = _blockHashRegister.get(room.blockNumber);
            if (blockHash == 0) {
                // skip as block is not actualised or not in register
                return;
            }
            _actualiseArea(location, blockHash);
            (uint8 exits, uint8 kind, uint8 area_discovered) = PureDungeon._generateRoom(
                location,
                blockHash,
                room.direction,
                room.areaAtDiscovery,
                room.lastRoomIndex,
                room.index
            );
            room.exits = exits;
            room.kind = kind;
            uint256 areaLoc = PureDungeon._getAreaLoc(location);
            Area storage area = _areas[areaLoc];
            if (area.discovered != area_discovered) {
                area.discovered = area_discovered;
            }
            emit RoomActualised(location, blockHash, exits, kind);
            uint8 areaType = _areas[areaLoc].areaType;
            if (room.discoverer != 0) {
                CharacterData memory characterData = _getCharacterData(room.discoverer);
                (uint256 numGold, uint256 numElements) = PureDungeon._computeRoomDiscoveryReward(
                    location,
                    blockHash,
                    characterData.class
                );
                if (areaType <= 5) {
                    _elementsContract.mint(room.discoverer, areaType, numElements);
                }
                _elementsContract.mint(room.discoverer, 6, numGold);
            }
        }
        _actualiseRandomEvent(PureDungeon._getAreaLoc(location)); // room actualisation take precedence // monster
    }

    function _actualiseArea(uint256 location, bytes32 blockHash) internal {
        uint256 areaLoc = PureDungeon._getAreaLoc(location);
        uint8 areaType = _areas[areaLoc].areaType;
        if (areaType == 0) {
            uint64 currentNumAreas = _areaCounter.numAreas;
            areaType = PureDungeon._generateArea(areaLoc, blockHash, currentNumAreas);
            _areas[areaLoc].areaType = areaType;
            if (areaType != 6) {
                uint64 period = uint64(block.timestamp / 23 hours);
                if (_areaCounter.lastPeriod != period) {
                    _areaCounter.lastPeriod = period;
                    _areaCounter.numAreas = 1;
                } else {
                    _areaCounter.numAreas = currentNumAreas + 1;
                }
            }
        }
    }

    struct CharacterData {
        uint8 class;
        uint16 level;
        uint32 xp;
        uint16 maxHP;
        uint16 hp;
    }

    function _setCharacterData(uint256 characterId, CharacterData memory characterData) internal {
        uint256 data = ((uint256(characterData.class) << 248) +
            (uint256(characterData.level) << 232) +
            (uint256(characterData.xp) << 200) +
            (uint256(characterData.maxHP) << 184) +
            (uint256(characterData.hp) << 168));
        _charactersContract.setData(characterId, data);
    }

    function _getCharacterData(uint256 characterId) internal view returns (CharacterData memory) {
        uint256 data = _charactersContract.getData(characterId);
        (uint16 level, uint16 hp, uint16 maxHP, uint32 xp, uint8 class) = PureDungeon._decodeCharacterData(data);
        return CharacterData(class, level, xp, maxHP, hp);
    }

    function _actualiseRandomEvent(uint256 areaLoc) internal {
        Area storage area = _areas[areaLoc];
        uint64 blockNumber = area.eventBlockNumber;
        if (blockNumber != 0) {
            bytes32 blockHash = _blockHashRegister.get(blockNumber);
            if (blockHash == 0) {
                // skip as block is not actualised or not in register
                return;
            }
            (uint256 roomLocation, uint64 randomEvent) = PureDungeon._generateRandomEvent(areaLoc, blockHash);
            uint256 monsterIndex = _checkMonsterBlockNumber(roomLocation);
            Room storage room = _rooms[roomLocation];
            if (room.randomEvent != 2
                && room.numActiveCharacters == 0
                && monsterIndex == 0
                && room.kind != 0
            ) {
                room.randomEvent = randomEvent;
            }
            area.eventBlockNumber = 0;
        }
    }

    /// @dev to be valid it require the room to be actualised first
    function _checkMonster(uint256 location) internal view returns (uint256) {
        uint256 monsterIndex = _checkMonsterBlockNumber(location);
        // if (monsterIndex == 0) {
        //     if (_roomEvents[location] == 1)  { //TODO monster indicator
        //         return 1;
        //     }
        //     return 0;
        // }
        return monsterIndex;
    }

    function _checkMonsterBlockNumber(uint256 location) internal view returns (uint256) {
        uint64 monsterBlockNumber = _rooms[location].monsterBlockNumber;
        if (monsterBlockNumber == 0) {
            // no monsters
            return 0;
        }
        bytes32 monsterBlockHash = _blockHashRegister.get(monsterBlockNumber);
        if (monsterBlockHash == 0) {
            // skip as monster block is not actualised
            return 0;
        }
        bool newlyDiscoveredRoom = monsterBlockNumber == _rooms[location].blockNumber;
        return
            PureDungeon._generateMonsterIndex(
                location,
                monsterBlockHash,
                1,
                newlyDiscoveredRoom,
                _rooms[location].kind
            );
    }

    struct GearData {
        uint16 level;
        uint8 slot;
        uint8 classBits; // bit array of allowed classes indexed by lsb
        uint16 durability;
        uint16 maxDurability; // gear is unbreakable when maxDurablity is 0
        uint32 template;
    }

    function _setGearData(uint256 gearId, GearData memory gear) internal {
        uint256 data = PureDungeon._encodeGearData(
            gear.level,
            gear.slot,
            gear.classBits,
            gear.durability,
            gear.maxDurability,
            gear.template
        );
        _gearsContract.setData(gearId, data);
    }

    function _getGearData(uint256 gearId) internal view returns (GearData memory) {
        uint256 data = _gearsContract.getData(gearId);
        (
            uint16 level,
            uint8 slot,
            uint8 classBits,
            uint16 durability,
            uint16 maxDurability,
            uint32 template
        ) = PureDungeon._decodeGearData(data);
        return GearData(level, slot, classBits, durability, maxDurability, template);
    }

    function _addInitialGears(uint256 characterId) internal {
        uint256 attackGearData = PureDungeon._encodeGearData(0, 0, 15, 10, 10, 1);
        uint256 defenseGearData = PureDungeon._encodeGearData(0, 1, 15, 10, 10, 4);
        uint256 attackGear = _gearsContract.mint(characterId, attackGearData);
        _equip(characterId, 0, 0, attackGear, 0);
        uint256 defenseGear = _gearsContract.mint(characterId, defenseGearData);
        _equip(characterId, 0, 0, defenseGear, 1);
    }

    // TODO restrict transfer of equiped items
    function _equip(
        uint256 characterId,
        uint16 level,
        uint8 class,
        uint256 id,
        uint8 slot
    ) internal {
        GearData memory gear = _getGearData(id);
        require(gear.level <= level, "gear Level too high");
        require((gear.classBits >> class) & 1 != 0, "invalid class");
        if (slot == 0) {
            require(gear.slot == 0, "only attack gear on slot 0");
            _characters[characterId].slot_1 = id;
        } else if (slot == 1) {
            require(gear.slot == 1, "only defense gear on slot 1");
            _characters[characterId].slot_2 = id;
        } else if (slot == 2) {
            require(gear.slot == 2, "only accessories on slot 2");
            _characters[characterId].slot_3 = id;
        } else if (slot == 3) {
            require(gear.slot == 2, "only accessories on slot 3");
            _characters[characterId].slot_4 = id;
        } else if (slot == 4) {
            require(gear.slot == 2, "only accessories on slot 4");
            _characters[characterId].slot_5 = id;
        }
        emit Equip(characterId, id, gear.slot);
    }

    function _handleKey(
        uint256 characterId,
        uint256 location,
        uint256 location2
    ) internal {
        uint256 location1 = location;
        if (location1 > location2) {
            location1 = location2;
            location2 = location;
        }
        if (!_isUnlocked(characterId, location1, location2)) {
            require(_elementsContract.subBalanceOf(characterId, PureDungeon.KEYS) > 0, "no key");
            _elementsContract.subBurnFrom(characterId, PureDungeon.KEYS, 1);
            _unlockedExits[characterId][location1][location2] = true;
        }
    }

    function _isUnlocked(
        uint256 characterId,
        uint256 location1,
        uint256 location2
    ) internal view returns (bool) {
        return _unlockedExits[characterId][location1][location2];
    }

    function _getAreaTypeForRoom(uint256 location) internal view returns (uint8) {
        return _areas[PureDungeon._getAreaLoc(location)].areaType;
    }

    function _moveTo(
        uint256 characterId,
        uint256 oldLocation,
        uint8 direction
    ) internal returns (uint256) {
        (int64 x, int64 y, int64 z, ) = PureDungeon._coordinates(oldLocation);
        if (PureDungeon.NORTH == direction) {
            y--;
        } else if (PureDungeon.EAST == direction) {
            x++;
        } else if (PureDungeon.SOUTH == direction) {
            y++;
        } else if (PureDungeon.WEST == direction) {
            x--;
        } else {
            revert("impossible direction");
        }
        uint256 newLocation = PureDungeon._location(x, y, z);
        Room storage currentRoom = _rooms[oldLocation];
        Room storage nextRoom = _rooms[newLocation];
        uint64 cb = currentRoom.blockNumber;
        uint64 nb = nextRoom.blockNumber;
        uint8 exitMask = uint8(2) ** direction;
        uint8 opositeExitMask = uint8(2) ** ((direction + 2) % 4);
        if (cb < nb || nb == 0) {
            if ((currentRoom.exits & exitMask) == exitMask) {
                if ((currentRoom.exits / 2 ** 4) & exitMask == exitMask) {
                    _handleKey(characterId, oldLocation, newLocation);
                }
                return newLocation;
            }
        } else if (cb > nb) {
            if ((nextRoom.exits & opositeExitMask) == opositeExitMask) {
                if ((nextRoom.exits / 2 ** 4) & opositeExitMask == opositeExitMask) {
                    _handleKey(characterId, oldLocation, newLocation);
                }
                return newLocation;
            }
        } else {
            if ((currentRoom.exits & exitMask) == exitMask || (nextRoom.exits & opositeExitMask) == opositeExitMask) {
                if (oldLocation > newLocation) {
                    if ((nextRoom.exits / 2 ** 4) & opositeExitMask == opositeExitMask) {
                        _handleKey(characterId, oldLocation, newLocation);
                    }
                } else {
                    if ((currentRoom.exits / 2 ** 4) & exitMask == exitMask) {
                        _handleKey(characterId, oldLocation, newLocation);
                    }
                }
                return newLocation;
            }
        }
        revert("cant move this way");
    }

    function _move(
        uint256 characterId,
        uint256 location,
        uint8 direction
    ) internal {
        Character storage character = _characters[characterId];
        Room storage currentRoom = _rooms[character.location];
        Room storage nextRoom = _rooms[location];
        uint64 blockNumber;
        if (nextRoom.blockNumber == 0) {
            _discoverRoom(location, characterId, direction);
        } else {
            // TODO should we actualiseRoom first, before monster ?
            // TODO can we remove num active characters check?
            if (
                nextRoom.monsterBlockNumber == 0
                    && nextRoom.numActiveCharacters == 0
                    && nextRoom.randomEvent != 2
                    && _roomsContract.getData(location) == 0
            ) {
                blockNumber = uint64(block.number);
                _blockHashRegister.request();
                if (nextRoom.monsterBlockNumber == 0) {
                    nextRoom.monsterBlockNumber = blockNumber;
                }
            }
            _actualiseRoom(location);
            address benefactor = _roomBenefactor(location);
            if (benefactor != address(0) && uint256(uint160(benefactor)) != _charactersContract.getSubOwner(characterId)) {
                _elementsContract.mintVault(benefactor, PureDungeon.FRAGMENTS, 1);
                emit RoomIncome(location, benefactor, PureDungeon.FRAGMENTS, 1);
            }
        }
        uint256 areaLoc = PureDungeon._getAreaLoc(location);
        Area storage area = _areas[areaLoc];
        if (area.eventBlockNumber == 0 && block.number % 3 == 0) {
            if (blockNumber == 0) {
                blockNumber = uint64(block.number);
                _blockHashRegister.request();
            }
            area.eventBlockNumber = blockNumber;
            emit RandomEvent(areaLoc, blockNumber);
        }
        currentRoom.numActiveCharacters--;
        nextRoom.numActiveCharacters++;
        character.location = location;
        character.direction = direction;
        _increaseHPXP(characterId);
    }

    function _increaseHPXP(uint256 characterId) internal {
        CharacterData memory characterData = _getCharacterData(characterId);
        if (characterData.hp < characterData.maxHP) {
            characterData.hp += 1;
            _setCharacterData(characterId, characterData);
        }
    }

    function _isRoomActive(uint256 location) internal view returns (bool) {
        address owner = _roomsContract.ownerOf(location);
        return owner == address(this);
    }

    function _roomBenefactor(uint256 location) internal view returns (address) {
        if (_isRoomActive(location)) {
            return address(uint160(_roomsContract.subOwnerOf(location)));
        } else {
            return address(0);
        }
    }

    function _pay(uint256 characterId, uint256 location, uint256 id, uint256 amount) internal {
        address benefactor = _roomBenefactor(location);
        if (benefactor != address(0)) {
            uint256 share = amount / 5;
            if (share > 0) {
                _elementsContract.subTransferFrom(characterId, 0, id, share);
                _elementsContract.transferFrom(address(this), benefactor, id, share);
                emit RoomIncome(location, benefactor, id, share);
            }
            _elementsContract.subBurnFrom(characterId, id, amount - share);
        } else {
            _elementsContract.subBurnFrom(characterId, id, amount);
        }
    }

    function _discoverRoom(
        uint256 location,
        uint256 discoverer,
        uint8 direction
    ) internal {
        Area storage area = _areas[PureDungeon._getAreaLoc(location)];
        if (area.lastRoom > 0) {
            // area is also actualised with room when necessary
            _actualiseRoom(area.lastRoom);
            area.lastRoomIndex = area.currentIndex;
            area.lastRoom = 0;
        }
        _elementsContract.subBurnFrom(discoverer, PureDungeon.FRAGMENTS, PureDungeon._discoveryCost(location));
        Room storage nextRoom = _rooms[location];
        nextRoom.blockNumber = uint64(block.number);
        nextRoom.monsterBlockNumber = uint64(block.number);
        nextRoom.direction = direction;
        nextRoom.areaAtDiscovery = area.discovered;
        nextRoom.index = area.currentIndex++;
        nextRoom.lastRoomIndex = area.lastRoomIndex;
        nextRoom.discoverer = discoverer;
        area.lastRoom = location;
        _blockHashRegister.request();
        uint256 discovererOwner = _charactersContract.getSubOwner(discoverer);
        _initializeTaxDueDate(discovererOwner);
        _roomsContract.mintId(location, discovererOwner);
        emit RoomDiscovered(location, uint64(block.number), nextRoom.direction);
    }

    function _initializeTaxDueDate(uint256 owner) internal {
        if (owner != 0 && _roomsContract.subBalanceOf(owner) == 0) {
            uint256 dueDate = block.timestamp + 5 days;
            _taxDueDate[address(uint160(owner))] = dueDate;
            emit RoomTaxPay(address(uint160(owner)), 0, dueDate);
        }
    }
}
","keccak256":"0x733160bc199104f585801b788acc558c02dc856858344d32a731427b4cb3ca96","license":"MIT"},"src/dungeon/PureDungeon.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

library PureDungeon {
    uint256 internal constant LOCATION_ZERO = 2**255;
    uint256 internal constant BOUNTY = 2**254;

    uint8 internal constant ROOM_TYPE_NORMAL = 1;
    uint8 internal constant ROOM_TYPE_TELEPORT = 2;
    uint8 internal constant ROOM_TYPE_TEMPLE = 3;
    uint8 internal constant ROOM_TYPE_LORE = 4;
    uint8 internal constant ROOM_TYPE_CARRIER = 5;

    uint8 internal constant EXITS_INERTIA = 87;
    uint8 internal constant EXITS_BIFURCATION = 40;
    uint8 internal constant EXITS_BOTH_BIFURCATION = 25;

    uint8 internal constant NORTH = 0;
    uint8 internal constant EAST = 1;
    uint8 internal constant SOUTH = 2;
    uint8 internal constant WEST = 3;
    uint8 internal constant DOWN = 4;

    uint8 internal constant COINS = 6;
    uint8 internal constant KEYS = 7;
    uint8 internal constant FRAGMENTS = 8;

    uint8 internal constant WARRIOR = 0;
    uint8 internal constant EXPLORER = 1;
    uint8 internal constant MAGE = 2;
    uint8 internal constant BARBARIAN = 3;

    uint8 internal constant CLASS_BONUS = 4; // 100/4 = 25%

    uint8 internal constant LEVEL_RING_WIDTH = 25;
    uint16 internal constant ELEMENT_AREA_10000_PROBA = 100; // 1%
    uint8 internal constant NUM_ELEMENT_AREA_PER_PERIOD = 10;

    uint8 internal constant LOCK_PROBABILITY = 40;
    uint8 internal constant TWO_LOCK_PROBABILITY = 10;

    uint8 internal constant MONSTER_SPAWN = 15;

    uint8 internal constant INITIAL_HP = 16;
    uint8 internal constant HP_INCREASE = 10;

    function coordinates(uint256 location)
        external
        pure
        returns (
            int64 x,
            int64 y,
            int64 z,
            uint64 a
        )
    {
        return _coordinates(location);
    }

    function _coordinates(uint256 location)
        internal
        pure
        returns (
            int64 x,
            int64 y,
            int64 z,
            uint64 a
        )
    {
        x = int64(uint64(location));
        y = int64(uint64(location / 2**64));
        z = int64(uint64(location / 2**128));
        a = uint64(location / 2**255); // = 1 for valid location
    }

    function location(
        int64 x,
        int64 y,
        int64 z
    ) external pure returns (uint256) {
        return _location(x, y, z);
    }

    function _location(
        int64 x,
        int64 y,
        int64 z
    ) internal pure returns (uint256) {
        return 2**255 + uint256(uint64(z)) * 2**128 + uint256(uint64(y)) * 2**64 + uint64(x);
    }

    function _locationToBounty(uint256 location) internal pure returns (uint256 bounty){
        return location - LOCATION_ZERO + BOUNTY;
    }

    function _bountyToLocation(uint256 bounty) internal pure returns (uint256 location){
        return location - BOUNTY + LOCATION_ZERO;
    }

    function generateMonsterIndex(
        uint256 location,
        bytes32 blockHash,
        uint256 numMonsters,
        bool newlyDiscoveredRoom,
        uint8 roomKind
    ) external pure returns (uint256) {
        return _generateMonsterIndex(location, blockHash, numMonsters, newlyDiscoveredRoom, roomKind);
    }

    function _generateMonsterIndex(
        uint256 location,
        bytes32 blockHash,
        uint256 numMonsters,
        bool newlyDiscoveredRoom,
        uint8 roomKind
    ) internal pure returns (uint256) {
        if (roomKind != ROOM_TYPE_NORMAL) {
            return 0;
        }
        uint8 spawnRate = MONSTER_SPAWN;
        if (newlyDiscoveredRoom) {
            spawnRate += 10;
        }
        bool hasMonster = uint256(keccak256(abi.encodePacked(location, blockHash, uint8(6)))) % 100 < spawnRate;
        if (hasMonster) {
            return (uint256(keccak256(abi.encodePacked(location, blockHash, uint8(7)))) % numMonsters) + 1;
        }
        return 0;
    }

    function computeRoomDiscoveryReward(
        uint256 location,
        bytes32 blockHash,
        uint8 class
    ) external pure returns (uint256 numGold, uint256 numElements) {
        return _computeRoomDiscoveryReward(location, blockHash, class);
    }

    function _computeRoomDiscoveryReward(
        uint256 location,
        bytes32 blockHash,
        uint8 class
    ) internal pure returns (uint256 numGold, uint256 numElements) {
        uint256 ring = _getRing(location, 0);
        uint256 target = ring / LEVEL_RING_WIDTH;
        if (target == 0) {
            target = 1;
        }
        numElements = (target / 2) + (uint256(keccak256(abi.encodePacked(location, blockHash, uint8(100)))) % target);
        if (numElements == 0) {
            numElements = 1;
        }
        if (MAGE == class) {
            numElements = numElements + numElements / CLASS_BONUS;
        }
        if (target > 8) {
            target = 8;
        }
        numGold = (target / 2) + (uint256(keccak256(abi.encodePacked(location, blockHash, uint8(101)))) % target);
        if (numGold == 0) {
            numGold = 1;
        }
        if (EXPLORER == class) {
            numGold = numGold + numGold / CLASS_BONUS;
        }
    }

    function discoveryCost(uint256 location) external pure returns (uint256 fragments) {
        return _discoveryCost(location);
    }

    function _discoveryCost(uint256 location) internal pure returns (uint256 fragments) {
        if (location == LOCATION_ZERO) {
            return 0;
        } else {
            uint256 ring = _getRing(location, 0);
            return 1 + ring / LEVEL_RING_WIDTH;
        }
    }

    function generateRandomEvent(uint256 areaLoc, bytes32 blockHash)
        external
        pure
        returns (uint256 roomLocation, uint64 randomEvent)
    {
        return _generateRandomEvent(areaLoc, blockHash);
    }

    function _generateRandomEvent(uint256 areaLoc, bytes32 blockHash)
        internal
        pure
        returns (uint256 roomLocation, uint64 randomEvent)
    {
        roomLocation = _getRoomLoc(
            areaLoc,
            uint8(uint256(keccak256(abi.encodePacked(areaLoc, blockHash, uint8(102)))) % 9),
            uint8(uint256(keccak256(abi.encodePacked(areaLoc, blockHash, uint8(103)))) % 9)
        );
        randomEvent = 2; // for now always monster TODO :uint64(uint256(keccak256(abi.encodePacked(areaLoc, blockHash, uint8(104)))));
    }

    function generateRoom(
        uint256 location,
        bytes32 blockHash,
        uint8 direction,
        uint8 areaAtDiscovery,
        uint8 lastIndex,
        uint8 index
    )
        external
        pure
        returns (
            uint8 exits,
            uint8 kind,
            uint8 area_discovered
        )
    {
        return _generateRoom(location, blockHash, direction, areaAtDiscovery, lastIndex, index);
    }

    function _generateRoom(
        uint256 location,
        bytes32 blockHash,
        uint8 direction,
        uint8 areaAtDiscovery,
        uint8 lastIndex,
        uint8 index
    )
        internal
        pure
        returns (
            uint8 exits,
            uint8 kind,
            uint8 area_discovered
        )
    {
        exits = _generateExits(location, blockHash, direction);
        (kind, area_discovered) = _getRoomKind(location, blockHash, areaAtDiscovery, lastIndex, index);
    }

    function _getRoomKind(
        uint256 location,
        bytes32 blockHash,
        uint8 areaAtDiscovery,
        uint8 lastIndex,
        uint8 index
    ) internal pure returns (uint8 kind, uint8 area_discovered) {
        uint256 areaLoc = _getAreaLoc(location);
        uint8 currentIndex = lastIndex;
        area_discovered = areaAtDiscovery;
        (int64 x, int64 y, , ) = _coordinates(location);
        if (x == 0 && y == 0) {
            // special case // cannot be discovered in batch in one block
            kind = ROOM_TYPE_TELEPORT;
            area_discovered = area_discovered | 1;
        } else {
            while (currentIndex <= index) {
                uint8 roll = uint8(
                    uint256(keccak256(abi.encodePacked(areaLoc, currentIndex, blockHash, uint8(3)))) % 20
                );
                if (!(area_discovered & 1 == 1) && (roll == 0 || currentIndex == 80)) {
                    // if roll or last room
                    kind = ROOM_TYPE_TELEPORT;
                    area_discovered = area_discovered | 1;
                } else if (
                    !(area_discovered & 2 == 2) &&
                    (roll == 1 ||
                        (currentIndex == 80) || // last room
                        (currentIndex == 79 && !(area_discovered & 1 == 1))) // second last room and teleport not found yet
                ) {
                    kind = ROOM_TYPE_TEMPLE;
                    area_discovered = area_discovered | 2;
                } else if (
                    !(area_discovered & 4 == 4) &&
                    (roll == 2 ||
                        (currentIndex == 80) || // last room
                        (currentIndex == 79 && !(area_discovered & 1 == 1)) || // second last room and teleport not found yet
                        (currentIndex == 79 && !(area_discovered & 2 == 2)) || // second last room and temple not found yet
                        (currentIndex == 78 && !(area_discovered & 1 == 1) && !(area_discovered & 2 == 2))) // third last room and neither teleport nor temple found yet
                ) {
                    kind = ROOM_TYPE_LORE;
                    area_discovered = area_discovered | 4;
                } else if (
                    !(area_discovered & 8 == 8) &&
                    (roll == 3 ||
                        (currentIndex == 80) || // last room
                        (currentIndex == 79 && !(area_discovered & 1 == 1)) || // second last room and teleport not found yet
                        (currentIndex == 79 && !(area_discovered & 2 == 2)) || // second last room and temple not found yet
                        (currentIndex == 79 && !(area_discovered & 4 == 4)) || // second last room and lore not found yet
                        (currentIndex == 78 && !(area_discovered & 1 == 1) && !(area_discovered & 2 == 2)) || // third last room and neither teleport nor temple found yet
                        (currentIndex == 78 && !(area_discovered & 1 == 1) && !(area_discovered & 4 == 4)) || // third last room and neither teleport nor lore found yet
                        (currentIndex == 78 && !(area_discovered & 4 == 4) && !(area_discovered & 2 == 2)) || // third last room and neither lore nor temple found yet
                        (currentIndex == 77 &&
                            !(area_discovered & 4 == 4) &&
                            !(area_discovered & 2 == 2) &&
                            !(area_discovered & 1 == 1))) // fourth last room and neither telport, temple nor lore found yet
                ) {
                    kind = ROOM_TYPE_CARRIER;
                    area_discovered = area_discovered | 8;
                } else {
                    kind = ROOM_TYPE_NORMAL;
                }
                currentIndex++;
            }
        }
    }

    function toLevelUp(uint16 level)
        external
        pure
        returns (
            uint16 xpRequired,
            uint256 coinsRequired,
            uint8 hpIncrease
        )
    {
        return _toLevelUp(level);
    }

    function _toLevelUp(uint16 level)
        internal
        pure
        returns (
            uint16 xpRequired,
            uint256 coinsRequired,
            uint8 hpIncrease
        )
    {
        uint16[11] memory xpRequirements = [0, 10, 49, 119, 208, 328, 524, 826, 1269, 3654, 6894];
        xpRequired = xpRequirements[level];
        if (level > 8) {
            coinsRequired = 492;
        } else {
            coinsRequired = (((1 + level) ** 3) / 5) + 8;
        }
        hpIncrease = HP_INCREASE;
    }

    function generateArea(
        uint256 areaLoc,
        bytes32 blockHash,
        uint64 numElementalAreaInPeriod
    ) external pure returns (uint8 areaType) {
        return _generateArea(areaLoc, blockHash, numElementalAreaInPeriod);
    }

    function _generateArea(
        uint256 areaLoc,
        bytes32 blockHash,
        uint64 numElementalAreaInPeriod
    ) internal pure returns (uint8 areaType) {
        if (_isCentreArea(areaLoc) || numElementalAreaInPeriod >= NUM_ELEMENT_AREA_PER_PERIOD) {
            areaType = 6;
        } else {
            uint256 roomRing = (_getAreaRing(areaLoc) * 9);
            if (roomRing > 0) {
                roomRing -= 4; // this set the value to be the one of the corner room nearest to the center
            }
            if (roomRing >= LEVEL_RING_WIDTH) {
                bool elementArea = uint8(uint256(keccak256(abi.encodePacked(areaLoc, blockHash, uint8(12)))) % 10000) <=
                    ELEMENT_AREA_10000_PROBA;
                if (elementArea) {
                    (, ,int64 z, ) = _coordinates(areaLoc);
                    areaType = 1 + uint8(int8(z % 5));
                } else {
                    areaType = 6;
                }
            } else {
                areaType = 6;
            }
        }
    }

    // distance between 2 location * 0.4
    function teleportTax(uint256 p1, uint256 p2) external pure returns (uint256) {
        return _teleportTax(p1, p2);
    }

    function _teleportTax(uint256 p1, uint256 p2) internal pure returns (uint256) {
        uint256 cost = (2 * _getRing(p1, p2)) / 5;
        if (cost == 0) {
            return 1;
        } else {
            return cost;
        }
    }

    function carrierCost(uint256 location) external pure returns (uint256) {
        return _carrierCost(location);
    }

    function _carrierCost(uint256 location) internal pure returns (uint256) {
        uint256 cost = (2 * _getRing(location, LOCATION_ZERO)) / 6;
        if (cost == 0) {
            return 1;
        } else {
            return cost;
        }
    }

    function recyclingReward(uint256 gearData) external pure returns (uint256) {
        return _recyclingReward(gearData);
    }

    function _recyclingReward(uint256 gearData) internal pure returns (uint256) {
        (uint16 level, , , uint16 durability, uint16 maxDurability, ) = _decodeGearData(gearData);
        uint256 reward = 1 + level / 2;
        if (durability == maxDurability) {
            reward += 1 + reward / 2;
        }
        return reward;
    }

    function hpCost(uint16 hp) external pure returns (uint256) {
        return _hpCost(hp);
    }

    function _hpCost(uint16 hp) internal pure returns (uint256) {
        return hp;
    }

    function getRing(uint256 p1, uint256 p2) external pure returns (uint256) {
        return _getRing(p1, p2);
    }

    function _getRing(uint256 p1, uint256 p2) internal pure returns (uint256) {
        (int64 x1, int64 y1, , ) = _coordinates(p1);
        (int64 x2, int64 y2, , ) = _coordinates(p2);
        int256 dx = x1 - x2;
        if (dx < 0) {
            dx = -dx;
        }
        int256 dy = y1 - y2;
        if (dy < 0) {
            dy = -dy;
        }
        if (dx > 2**64 / 2) {
            dx = int256(2**64) - dx;
        }
        if (dy > 2**64 / 2) {
            dy = int256(2**64) - dy;
        }
        if (dx > dy) {
            return uint256(dx);
        } else {
            return uint256(dy);
        }
    }

    function _getAreaRing(uint256 areaLoc) internal pure returns (uint64) {
        (int64 x, int64 y, , ) = _coordinates(areaLoc);
        if (x < 0) {
            x = -x;
        }
        if (y < 0) {
            y = -y;
        }
        if (x > y) {
            return uint64(int64(x));
        } else {
            return uint64(int64(y));
        }
    }

    // centre area is currently defined as the first 9 area (first area + 8 surrounding it)
    function _isCentreArea(uint256 areaLoc) internal pure returns (bool) {
        (int64 areaX, int64 areaY, , ) = _coordinates(areaLoc);
        return areaX >= -1 && areaY >= -1 && areaX <= 1 && areaY <= 1;
    }

    function getAreaLoc(uint256 location) external pure returns (uint256) {
        return _getAreaLoc(location);
    }

    function _getAreaLoc(uint256 location) internal pure returns (uint256) {
        (int64 x, int64 y, int64 z, ) = _coordinates(location);
        int64 areaX;
        if (x >= 0) {
            areaX = (x + 4) / 9;
        } else {
            areaX = -((-x + 4) / 9);
        }

        int64 areaY;
        if (y >= 0) {
            areaY = (y + 4) / 9;
        } else {
            areaY = -((-y + 4) / 9);
        }
        return _location(areaX, areaY, z);
    }

    function _getRoomLoc(
        uint256 areaLoc,
        uint8 x,
        uint8 y
    ) internal pure returns (uint256) {
        (int64 areaX, int64 areaY, int64 floor, ) = _coordinates(areaLoc);
        return _location(areaX * 9 - 4 + int64(uint64(x)), areaY * 9 - 4 + int64(uint64(y)), floor);
    }

    // direction based exit generation
    // both exits and locks are generated
    // return value is encoded (uint4 locksBits, uint4 exitBits)
    function _generateExits(
        uint256 location,
        bytes32 blockHash,
        uint8 direction
    ) internal pure returns (uint8) {
        uint8 exits = 0;
        if (DOWN == direction) {
            exits = 0xF;
        } else {
            if (EXITS_INERTIA > uint8(uint256(keccak256(abi.encodePacked(location, blockHash, uint8(1)))) % 100)) {
                exits = uint8(2) ** direction;
            }
            if (EXITS_BIFURCATION > uint8(uint256(keccak256(abi.encodePacked(location, blockHash, uint8(2)))) % 100)) {
                if (
                    EXITS_BOTH_BIFURCATION >
                    uint8(uint256(keccak256(abi.encodePacked(location, blockHash, uint8(3)))) % 100)
                ) {
                    if (NORTH == direction || SOUTH == direction) {
                        exits = (exits | 8) | 2;
                    } else if (EAST == direction || WEST == direction) {
                        exits = (exits | 1) | 4;
                    }
                } else {
                    if (50 > uint8(uint256(keccak256(abi.encodePacked(location, blockHash, uint8(3)))) % 100)) {
                        if (NORTH == direction) {
                            exits |= 8;
                        } else if (EAST == direction) {
                            exits |= 1;
                        } else if (SOUTH == direction) {
                            exits |= 2;
                        } else if (WEST == direction) {
                            exits |= 4;
                        }
                    } else {
                        if (NORTH == direction) {
                            exits |= 2;
                        } else if (EAST == direction) {
                            exits |= 4;
                        } else if (SOUTH == direction) {
                            exits |= 8;
                        } else if (WEST == direction) {
                            exits |= 1;
                        }
                    }
                }
            }
        }
        uint8 randLock = uint8(uint256(keccak256(abi.encodePacked(location, blockHash, uint8(111)))) % 100);
        uint8 numLocks = randLock < (100 - LOCK_PROBABILITY) ? 0 : (randLock < (100 - TWO_LOCK_PROBABILITY) ? 1 : 2);
        if (numLocks >= 4) {
            exits = exits | (15 * uint8(2) ** 4);
        } else if (numLocks == 3) {
            uint8 chosenLocks = uint8(uint256(keccak256(abi.encodePacked(location, blockHash, uint8(112)))) % 4);
            uint8 locks = (chosenLocks + 1) * 7;
            if (locks == 21) {
                exits = exits | (13 * uint8(2) ** 4);
            } else if (locks == 28) {
                exits = exits | (11 * uint8(2) ** 4);
            }
            // 4 possibilities : 7 // 14 // 13 // 11
        } else if (numLocks == 2) {
            uint8 chosenLocks = uint8(uint256(keccak256(abi.encodePacked(location, blockHash, uint8(112)))) % 6);
            uint8 locks = (chosenLocks + 1) * 3;
            if (locks == 15) {
                exits = exits | (5 * uint8(2) ** 4);
            } else if (locks == 18) {
                exits = exits | (10 * uint8(2) ** 4);
            }
            // 3 // 6 // 9 // 12 // 5 // 10
        } else if (numLocks == 1) {
            uint8 chosenLocks = uint8(uint256(keccak256(abi.encodePacked(location, blockHash, uint8(112)))) % 4);
            exits = exits | (uint8(2) ** chosenLocks * uint8(2) ** 4);
        }
        return exits;
    }

    function decodeCharacterData(uint256 data)
        external
        pure
        returns (
            uint16 level,
            uint16 hp,
            uint16 maxHP,
            uint32 xp,
            uint8 class
        )
    {
        return _decodeCharacterData(data);
    }

    function _decodeCharacterData(uint256 data)
        internal
        pure
        returns (
            uint16 level,
            uint16 hp,
            uint16 maxHP,
            uint32 xp,
            uint8 class
        )
    {
        class = uint8(data >> 248);
        level = uint16((data >> 232) % 2**16);
        xp = uint32((data >> 200) % 2**32);
        maxHP = uint16((data >> 184) % 2**16);
        hp = uint16((data >> 168) % 2**16);
    }

    function encodeGearData(
        uint16 level,
        uint8 slot,
        uint8 classBits,
        uint16 durability,
        uint16 maxDurability,
        uint32 template
    ) external pure returns (uint256 data) {
        return _encodeGearData(level, slot, classBits, durability, maxDurability, template);
    }

    function _encodeGearData(
        uint16 level,
        uint8 slot,
        uint8 classBits,
        uint16 durability,
        uint16 maxDurability,
        uint32 template
    ) internal pure returns (uint256 data) {
        return ((uint256(classBits) << 248) +
            (uint256(level) << 232) +
            (uint256(slot) << 224) +
            (uint256(durability) << 208) +
            (uint256(maxDurability) << 192) +
            template);
    }

    function decodeGearData(uint256 data)
        external
        pure
        returns (
            uint16 level,
            uint8 slot,
            uint8 classBits,
            uint16 durability,
            uint16 maxDurability,
            uint32 template
        )
    {
        return _decodeGearData(data);
    }

    function _decodeGearData(uint256 data)
        internal
        pure
        returns (
            uint16 level,
            uint8 slot,
            uint8 classBits,
            uint16 durability,
            uint16 maxDurability,
            uint32 template
        )
    {
        classBits = uint8(data >> 248);
        level = uint16((data >> 232) % 2**16);
        slot = uint8((data >> 224) % 2**8);
        durability = uint16((data >> 208) % 2**16);
        maxDurability = uint16((data >> 192) % 2**16);
        template = uint32(data % 2**32);
    }

    function limitedChange(
        uint16 value,
        uint16 max,
        int64 change
    ) external pure returns (uint16) {
        return _limitedChange(value, max, change);
    }

    function _limitedChange(
        uint16 value,
        uint16 max,
        int64 change
    ) internal pure returns (uint16) {
        int64 updated = int64(uint64(value)) + int64(change);
        if (updated > int64(uint64(max))) {
            return max;
        }
        if (updated <= 0) {
            return 0;
        } else {
            return uint16(int16(updated));
        }
    }

    function roomsTax(uint256 rooms, uint256 periods) external pure returns (uint256) {
        return _roomsTax(rooms, periods);
    }
    function _roomsTax(uint256 rooms, uint256 periods) internal pure returns (uint256) {
        return (1 + rooms / 10) * periods;
    }
}
